package amazing_subarray

import (
	"fmt"
	"testing"
)

type TestCase struct {
	input    string
	expected int
}

func TestFindCountOfAmazingSubarray(t *testing.T) {
	testCases := make([]TestCase, 0)
	// testCases = append(testCases, TestCase{
	// 	input:    "ABEC",
	// 	expected: 6,
	// })

	testCases = append(testCases, TestCase{
		input:    "",
		expected: 2099,
	})
	for _, testcase := range testCases {
		t.Run(fmt.Sprintf("testing %s", testcase.input), func(tb *testing.T) {
			got := FindCountOfAmazingSubarray(testcase.input)
			if got != testcase.expected {
				tb.Errorf("Tested %s to  expected %d but got %d", testcase.input, testcase.expected, got)
			}
		})
	}
}
